import { useState } from "react";
import Card from "./Card";
import ExpensItem from "./ExpensItems";
import ExpensesFilter from "./ExpenseFilter";
import './Expenses.css';



 const Expenses=(props)=>{
  const expenses=props.item;
  const[filteredYear,setFilteredYear]=useState('2020')

  const DateSelectHandler=selectedYear=>{
      setFilteredYear(selectedYear)
  }
    return(
      <div>
     <Card className="expenses">
      <ExpensesFilter selected={filteredYear} onChange={DateSelectHandler}/>
      {props.item.map(expenses=>ex)}
      <ExpensItem title={expenses[0].title} date={expenses[0].date} amount={expenses[0].amount}></ExpensItem>
      <ExpensItem title={expenses[1].title} date={expenses[1].date} amount={expenses[1].amount}></ExpensItem>
      <ExpensItem title={expenses[2].title} date={expenses[2].date} amount={expenses[2].amount}></ExpensItem>
      <ExpensItem title={expenses[3].title} date={expenses[3].date} amount={expenses[3].amount}></ExpensItem>
     </Card>
     </div>
    )


}
export default Expenses;
